mapper.select:
select distinct dn_rev_meal_time as mealTime
from sh_dn_rev
where dn_no = #{value}

select dn_rev_time
from sh_dn_rev
where dn_no = #{no}
and dn_rev_meal_time = #{mealTime}
and dn_rev_week = #{week}

mapper.delete:
delete from
	sh_rt_rev_count
where rt_rev_count_meal_time = #{mealTime}
and rt_rev_count_seat_type = #{seatType}
and rt_rev_count = #{revCount}
